    private static void insertTheBestCars(SQLiteDatabase db, String animal, int animaImagelId, String year, String brand, int carImageId, String engine, String weight, String bhp, String accel, String speed, String price, String description){
        ContentValues carsValues = new ContentValues();
        carsValues.put("ANIMAL", animal);
        carsValues.put("ANIMAL_IMAGE", animaImagelId);
        carsValues.put("YEAR", year);
        carsValues.put("BRAND", brand);
        carsValues.put("CAR_IMAGE", carImageId);
        carsValues.put("ENGINE", engine);
        carsValues.put("WEIGHT", weight);
        carsValues.put("BHP", bhp);
        carsValues.put("ACCEL", accel);
        carsValues.put("SPEED", speed);
        carsValues.put("PRICE", price);
        carsValues.put("DESCRIPTION", description);
        db.insert("THEBEST", null, carsValues);
    }


    private static void insertNewCars(SQLiteDatabase db, int logo, String year, String brand, String model, int carimage, int side, String engine, String weight, String bhp, String accel, String speed, String price, String descr){
        ContentValues newCarsValues = new ContentValues();
        newCarsValues.put("LOGO", logo);
        newCarsValues.put("YEAR", year);
        newCarsValues.put("BRAND", brand);
        newCarsValues.put("MODEL", model);
        newCarsValues.put("CAR_IMAGE", carimage);
        newCarsValues.put("SIDE_IMAGE", side);
        newCarsValues.put("ENGINE", engine);
        newCarsValues.put("WEIGHT", weight);
        newCarsValues.put("BHP", bhp);
        newCarsValues.put("ACCEL", accel);
        newCarsValues.put("SPEED", speed);
        newCarsValues.put("PRICE", price);
        newCarsValues.put("DESCRIPTION", descr);
        db.insert("NEWCARS", null, newCarsValues);
    }


